float rand(float n){return fract(sin(n) * 43758.5453123);}

float noise(float p){
	float fl = floor(p);
  float fc = fract(p);
	return mix(rand(fl), rand(fl + 1.0), fc);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 leftEye = vec2(0.35, 0.5);
    vec2 rightEye = vec2(0.65, 0.5);
    vec2 head = vec2(0.5);
    vec2 body = vec2(0.5, 0.1);
    vec2 mouth = vec2(0.5, 0.3);
    uv.y += 0.1 * sin(iTime);
    float headDist = distance(uv, head);
    float leftDist = distance(uv, leftEye);
    float rightDist = distance(uv, rightEye);
    float bodyDist = distance(uv , body);
    float mouthDist = distance(uv, mouth);

    vec3 tile = vec3(1.0);
    tile *= vec3(1.0-headDist,1.0-step(0.3, headDist),0.0);
    tile *= vec3(step(min(0.1, abs(sin(32901.*uv.y*15.))), rightDist));
    tile *= vec3(step(min(0.1, abs(sin(32901.*uv.y*15.))), leftDist));
    tile *= vec3(step(0.01, noise(mouthDist)));
    tile += vec3(0.0, 1.0-step(0.2, sqrt(bodyDist*uv.y+0.025)),0.0);

    vec3 color = vec3(tile);
    fragColor = vec4(color,1.0);
}
