// variant of "Woodcut" by Krabcode. https://shadertoy.com/view/wstcDM

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    vec4 wood = texture(iChannel0, U/R);
    float   l = length(textureLod(iChannel1, U/R, 1.).rgb),
          cut = smoothstep(1., 0., l),
         illu = dot( vec2(dFdx(cut),dFdy(cut)) *R.y/100.,
                     cos(iTime+vec2(0,11)));
    O = wood * ( 1.-.5*illu - .2*cut );
}
