#define S(v) smoothstep(1.5*dp, 0., abs(v) )   

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy, 
         D = vec2(0,R.y);
         U = 2.* (2. * U - D ) / R.y;

    float dp = 4./R.y,
           v = 1e9;

    for (float x=0.; x < dp ; x += 1e-5 )
        v = min( v, abs( U.y - F(U.x+x) ));

    O = vec4( S(v) );
}

vec2 mainSound( in int samp, float time )
{
    return vec2(F(time));
}

const float PI2 = 6.283185307179586;
const int _A  = 65;
