vec2 cmul (vec2 c1, vec2 c2)
{
	float a = c1.x;
	float b = c1.y;
	float c = c2.x;
	float d = c2.y;

	return vec2 (a * c - b * d, b * c + a * d);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 c = fragCoord.xy / iResolution.xy;
    //scaling real axis to [-2.5, 1.5] and  imaginary axis to  [-1.5, 1.5]/
    //Eyeballed coordinates to center the fractal
    c = c * vec2(1,.7) - vec2(.55, .35); 

    vec2 z = c;
    vec2 oldz = c;
    vec2 z1 = c;
    fragColor = vec4(0);

    int i = 0;

    for (;i<100;i++)
    {
       oldz = z;
       if (length(z) >= 2.) 
       {
           break;
       }
       z = cmul(z, z) + z1 + c;
       z1 = oldz;
    }

    fragColor = vec4(i)/100.;

}
