#define pi 3.14159565

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 r = 2.*fragCoord/iResolution.xy -1.;
	// Colors
    vec3 background = vec3(0.,0.4,1.);
	vec3 wood = vec3(0.85,.63,0.43);
    vec3 iron = vec3(0.685,.63,0.73);
    // Shaft 
    float bottom = -0.6;
    float top = 0.4;
    float left = -0.03;
    float right =0.05;
    // Iron
    float ibottom = 0.38;
    float itop = 0.57;
    float ileft = -0.055;
    float iright =0.25;
    vec3 color = background;

    //left side of the handle rendering
    vec2 p = r;
    p.y = (p.y-bottom)/(top-bottom);
    p.x += 0.08*pow(p.y,3.)*sin(pi*p.y);
    float f = smoothstep(left,left +0.008,p.x);

    //right side of the handle rendering
	p = r;
    p.y = (p.y-bottom)/(top-bottom);    
    p.x -= 0.02*pow(p.y,2.)*(sin(2.*pi*p.y));
    f*=(1.-smoothstep(right,right+0.008,p.x));

    p = r;
    f*=smoothstep(bottom,bottom+0.01,p.y)*(1.-smoothstep(top,top+0.01,p.y));
    vec3 col = mix(wood, vec3(0.,0.,0.), exp(-3.*pow(abs(top-p.y),.5)));
    color = mix(color, col, f);

    float a =0.05;
    float z =pow(length(r-vec2(0.03,0.43))-0.2*r.y,2.);
    col = mix(iron, vec3(1.),10.* z);
    f = smoothstep(ileft,ileft +0.008,p.x);
    f*=1.-smoothstep(a,a+0.01,z);

    //upper side of the iron    
    p.x = (p.x-ileft)/(iright-ileft);
    p.y-=0.15*pow(.9*p.x,4.);    
    f*=(1.-smoothstep(itop,itop+0.01,p.y));
    //lower side of the iron    
    p=r;
    p.x = (p.x-ileft)/(iright-ileft);
    p.y+=0.15*pow(p.x,2.9); 
    f*=smoothstep(ibottom,ibottom+0.01,p.y);
    color = mix(color, col, f);



    // Output to screen
    fragColor = vec4(color,1.0);
}
