void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float amp = 0.1;
    float freq = 40.0;

    // stacking sin waves

    float x = uv.x;
    float y = amp * sin(x * freq + iTime);
    y += amp * sin(x*freq*2.5+iTime*1.2);

    vec3 col = 1.0-vec3(pow(uv.y - y+.5,15.0));
	col.gb = vec2(0.3);

    fragColor = vec4(col,1.0);
}
