#define t iTime*.1

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;    
    if (uv.x > 0.5)
    	uv.x = 1. - uv.x;

    uv.x = (uv.x+0.25) * 0.5* pow(uv.x+0.25, 2.0);
    uv.y = (uv.y-0.5) * (uv.x*20.);
    uv.x += t;

    vec4 noise = texture(iChannel0, uv);

    fragColor = noise;
}
