// by Nikos Papadopoulos, 4rknova / 2019
// Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

#define ZOOM (7.5)
#define WGHT (0.1)
#define T (iTime)
#define R (iResolution.xy)

#define HASH(p) (fract(sin(dot(p,vec2(127.1,311.7))) * 43758.5453123))
#define CIRCLE(p, c, r) (abs(r - length(p - c)))

float sharpen(in float d, in float w)
{
    /* The division is specific to wide aspect ratio.
	** Replace with min(R.x,R.y) if in portrait mode.
	** Same goes for aspect ratio correction below.
    */
    float e = 1.5 * ZOOM / R.y;
    return 1. - smoothstep(-e, e, d - w);
}

float df_pattern(vec2 uv)
{
    float l1 = sharpen(CIRCLE(uv, vec2(0), .5), WGHT);
    float l2 = sharpen(CIRCLE(uv, vec2(1), .5), WGHT);
    return max(l1,l2);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = (fragCoord/R*2.-1.) * ZOOM * vec2(R.x/R.y,1.); 
    vec2 st = floor(uv), p = fract(uv);    
    float k0 = step(.5, HASH(st + floor(T+1.0)));
    float k1 = step(.5, HASH(st + floor(T+2.0)));    
    vec2 s0 = vec2(k0 + p.x - 2.*k0*p.x, p.y);
    vec2 s1 = vec2(k1 + p.x - 2.*k1*p.x, p.y);
    vec3 c = vec3(mix(df_pattern(s0), df_pattern(s1),fract(iTime)));
    fragColor = vec4(c, 1);
}
