vec4 k;
//frag Color,Coord
void mainImage(out vec4 a,in vec2 b){

	vec2 u=(b.xy/iResolution.y);

    vec3 c=vec3(0.),
    p=vec3(u,2.+length(u)),    
    o=p.xzy,
    q=vec3(-2.3,1.19,-3.4)+.601*vec3(.3,.98,.034);

    float T=iTime/20.,
    S=sin(T),
    C=cos(T),
    x=o.x,
    z=o.z;    

    o.xz=vec2(S*x-C*z,C*x+S*z);

	z=0.;
	for(int i=0;i<45;++i){
		p=q+z*o;
        k=vec4(0.);
        for(int i=0;i<33;++i){
            x=dot(p,p);
            k+=vec4(p=abs(p)/x,x);
            p-=vec3(.5,.4,1.5);
        };
        k/=32.;
		z+=max(
            0.001,
            min(0.01,x=1.-abs(1.-x))
        )*(
            .3+3.*z
        );
		c+=(
            .5+.5*sin(k.rgb*30.)
        )/(
            1.+x*x*1000.
        );
	};

	a=vec4(
        pow(
            clamp(c/5.,0.,1.),
            vec3(.7)
        ),
        1.
    );	
}
