//The colors for the checkerboard
vec4 color1 = vec4(1.0, 1.0, 1.0, 1.0);
vec4 color2 = vec4(0.0, 0.0, 0.0, 1.0);

//Controls how large the checkerboxes are
float boxSize = 20.0;

//Determines whether or not to use textures instead of colors
bool useTextures = true;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 col;

    if(useTextures)
    {
        color1 = texture(iChannel0, uv);
        color2 = texture(iChannel1, uv);
    }


    col = color1;
    //Only changes the color if both the x and y of the uv both are equally divisible by 2.
    if(mod(floor(uv.x * boxSize) + floor(uv.y * boxSize), 2.0) == 0.0)
    {
     	col = color2;   
    }


    // Output to screen
    fragColor = col;
}
