// colors
vec4 color = vec4(1.0, 0.0, 0.0, 1.0);
vec4 ucolor = vec4(0.0, 0.0, 1.0, 1.0);
vec4 border = vec4(1.0, 0.96, 0.24, 1.0);

float blur_amount = 0.007;

vec4 checkcolor(in vec2 xy, in float fade)
{
    // dissolve amount
    float dis = texture(iChannel0, xy).r;

    // translate between colors
    if (dis < fade)
    {
        // "under" color
        return ucolor;
    }

    // nice yellow border
    if (dis < fade * 1.2)
    {
        // border
        return border;
    }

    // default color
    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // time fade
    float fade = abs(sin(iTime / 3.14));

    // base color
    vec2 xy = fragCoord.xy / iResolution.xy;
    vec4 fcolor = checkcolor(xy, fade);

    // add some blur
    xy = fragCoord.xy / iResolution.xy + vec2(blur_amount, 0.0);
    fcolor += checkcolor(xy, fade);
    xy = fragCoord.xy / iResolution.xy + vec2(-blur_amount, 0.0);
    fcolor += checkcolor(xy, fade);    
    xy = fragCoord.xy / iResolution.xy + vec2(0.0, blur_amount);
    fcolor += checkcolor(xy, fade);
    xy = fragCoord.xy / iResolution.xy + vec2(0.0, -blur_amount);
    fcolor += checkcolor(xy, fade);
    xy = fragCoord.xy / iResolution.xy + vec2(blur_amount, blur_amount);
    fcolor += checkcolor(xy, fade);
    xy = fragCoord.xy / iResolution.xy + vec2(-blur_amount, blur_amount);
    fcolor += checkcolor(xy, fade);
    xy = fragCoord.xy / iResolution.xy + vec2(blur_amount, -blur_amount);
    fcolor += checkcolor(xy, fade);
    xy = fragCoord.xy / iResolution.xy + vec2(-blur_amount, -blur_amount);
    fcolor += checkcolor(xy, fade);

    fcolor /= 9.0;

    // add some texture
    xy = fragCoord.xy / iResolution.xy;
    fragColor = fcolor + texture(iChannel1, xy);
}
