float Spikes(float angle, float armSpeed, float armNum, float armWidth, float size)
{
    float spikes =  -(abs(cos(angle * 0.5 + (iTime * armSpeed)) 
                        * sin(angle * armNum )))
        											* armWidth +      size;

    return spikes;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Colors in vec3
    vec3 red = vec3(0.8 ,0.0 ,0.0);
    vec3 white = vec3(1.0, 1.0, 1.0);
    vec3 yellow = vec3(0.9, 0.9, 0.3);
    vec3 blue = vec3(0.0, 0.2, 1.0);
    vec3 black = vec3(0.0, 0.0, 0.0);
    vec3 green = vec3(0.0, 1.0, 0.0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float ratio = iResolution.x/iResolution.y;
    uv.x *= ratio;

    vec2 shapePos = vec2((0.5 * ratio) , 0.5);
    float dist = distance(shapePos , uv) * 2.50;

    uv -= shapePos;

    float angle = atan(uv.y, uv.x);
    float spikes = Spikes(angle, 0.25, 6.0, 10.0, 0.5);


    vec3 test = mix(white, green, spikes);
    test = mix(test, red, Spikes(angle, 3.0, 6.0, 10.0, 0.5));

    float value =  step(spikes * 3.0, dist);

    // Output to screen
    fragColor = vec4(test , 1.0);
}
