#define PI 3.1415926538

vec2 checks = vec2(10.0);


float square_wave(in float x) {
    //return sign(sin(x*PI));
    //float w = 50.0/iResolution.x;
    //return smoothstep(-w,w,sin(x*PI))*2.-1. ;
	float f = sin(x*PI), w = fwidth(f);

	return smoothstep(-w,w,f)*2.-1.;
} 




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 mouse = iMouse.xy;

    float ratio = iResolution.x/iResolution.y;

    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = 
        vec3(
			square_wave( ratio*uv.x*checks.x + sin(uv.y*PI + iTime) + 2.0*PI*mouse.x/iResolution.x )
		*
			square_wave( uv.y*checks.y + sin(uv.x*PI + iTime) + 2.0*PI*mouse.y/iResolution.y )
    	);

    fragColor = vec4(col,1.0);
}
