void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    // In studio -> VertexAttribute patch tex coordinates
    vec2 uv = fragCoord/iResolution.xy;

    // Center the UV's 
	uv -= 0.5;
    const float kPi = 3.14159265358979;
    // Do this in fragment stage (fragment page) 
    float angle = atan(uv.y, uv.x) + kPi; // Angle [0.0, 2 * kPi]
    float shade = angle / (kPi * 2.0);

    // Output to screen 
    fragColor = vec4(vec3(shade),1.0);
}
