void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
	vec4 texColor = texture(iChannel0, uv); 

    texColor.r *= abs(sin(iTime));
    texColor.g *= abs(cos(iTime));
    texColor.b *= abs(sin(iTime) * cos(iTime));    
    fragColor = texColor; 
}
