void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
	float pixelSize = 2.0 / iResolution.y;


    //uv.x = sqrt(abs(uv.x));
    uv.x*=0.80;
    uv.y+=0.3;
    uv.y -= sqrt(abs(sin(uv.x *iTime*2.5))) *0.7  ;


    float radius=0.6;
    float circle = 1.0 - smoothstep(radius - pixelSize, radius + pixelSize, length(uv));



    fragColor = vec4(circle );


}
