void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Normalized pixel coordinates (from 0 to 1
    float speed = 10.0;

    float time = mod(iTime,speed) /speed;

    float radius = mod(iTime,6.0);

    float cosine = cos(radius);
    float sine = sin(radius);


    vec2 uv = (fragCoord) / iResolution.xy;
    uv = uv +time;
    uv.x = uv.x * cosine - uv.y * sine;
    uv.y = uv.x * sine + uv.y * cosine;
    float freq = 10.0;
    vec2 pos = fract(uv*freq);
	vec3 color = vec3(1.0,1.0,1.0);

 ;	float strip = mod(pos.y,0.5);


    if(pos.y < 0.5){

           color = mix(vec3(0.0,0.0,0.0),vec3(1.0,1.0,1.0),strip);

    }else {
         ;	float strip2 = mod(-pos.y,0.5);
     color = mix(vec3(0.0,0.0,0.0),vec3(1.0,1.0,1.0),strip2);

    }



    fragColor =vec4(color.xyz,1.0);
}
