vec2 getUV(vec2 coord) {
    return ((coord-iResolution.xy*.5)/iResolution.y+.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = getUV(fragCoord);
	vec2 m = getUV(iMouse.xy);



    vec2 delta = (uv-m);

    float C = 300.1;
    float k = 10.4;
    vec2 thing =k* (delta) / pow(1.+dot(delta, delta), C);


    uv -= thing;

    // Output to screen
    fragColor = texture(iChannel0, uv);



    //fragColor += clamp(1.0-abs(length(uv-m)-.25)*100., 0., 1.);
}
