float rose(float w,float t,vec2 p)
{
    float x=0.;
    float y=0.;
    float q=0.;

	q=w*length(p);
    x=cos(q)*p.x-sin(q)*p.y;
    y=sin(q)*p.x+cos(q)*p.y;
    return fract(5.*(abs(x)+abs(y))-iTime+t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p=(fragCoord.xy*2.0-iResolution.xy)/iResolution.y;

	float r=rose(2.,0.,p);
    float g=rose(-2.,0.33,p);
    float b=rose(0.,066.,p);

    fragColor=vec4(r,g,b,1.);

}
