vec2 R;
vec4 T(vec2 U) {
	return texture(iChannel0, U/R);
}
void mainImage( out vec4 C, in vec2 U )
{ R = iResolution.xy;
 C = 0.5*T(U);
 vec2 r = vec2(sin(iTime),cos(iTime));
 C.x += 0.25*T(U-3.*r).x;
 C.x += 0.25*T(U-2.*r).x;
 C.x += 0.50*T(U-1.*r).x;
 C.y += 2.*C.y;
 C.z += 0.25*T(U-3.*r).z;
 C.z += 0.25*T(U-2.*r).z;
 C.z += 0.50*T(U-1.*r).z;
 C = C/float(iFrame);
 C = 0.5*C*C;
}

vec2 R;
vec3 hash33(vec3 p3) // Dave Hoskins
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.xxy + p3.yxx)*p3.zyx);

}
vec3 P (vec3 p) {
    p = floor(p+0.5);
    return (p + hash33(p+1.));
}
float voro (vec3 p, out vec3 norm) {
	float r = 1e8;
    vec3 q = vec3(r);
    for (int x = -2; x <= 2; x++) {
        for (int y = -2; y <= 2; y++) {
            for (int z = -2; z <= 2; z++) {
              	vec3 u = P(p+vec3(x,y,z));
                float t = length(p-u);
                if (t < r) {
                	r = t;
                    q = u;
                }
            }
        }
    }
    float l = 1e8;
    for (int x = -2; x <= 2; x++) {
        for (int y = -2; y <= 2; y++) {
            for (int z = -2; z <= 2; z++) {
              	vec3 u = P(p+vec3(x,y,z));
                vec3 mp = 0.5*(q+u);
                vec3 n = normalize(q-mp);
                float len = dot(p-mp,n);
                if (len < l) {
                	l = len;
                    norm = n;
                }
            }
        }
    }
    return l;
}
vec4 T(vec2 U) {
	return texture(iChannel0, U/R);
}
void mainImage( out vec4 C, in vec2 U )
{ R = iResolution.xy;
 	C = T(U);
 	vec2 h = .5*(hash33(vec3(U,iFrame)).xy-0.5);
 	U += h*abs(h);
 	U = 2.*(U-0.5*R)/R.y;
 	vec3 p = vec3(1.5*U+1000.,-1),d = normalize(vec3(U,1.5)),n;
 	float r = voro(p+d*C.w,n);
 	float o = (.5+0.5*dot(n,d));
 	C.xyz += o*(0.5+0.5*n)*exp(-4.*r)/(.2+0.1*C.w);
 	C.w += max(r,1e-3*C.w);
 	if (mod(float(iFrame),200.)<1.) C.w = 0.;
 if (iFrame < 1)
 	C = vec4(0);
}
