void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 col1=texture(iChannel0, fragCoord/iResolution.xy) ,
        col2=1. -(1. -col1)*(1. -col1), 
        col3=vec4(vec2(min(col2.r*0.2+col2.g*1.,1.)), col2.ba), 
        col5=vec4(vec3(1.-dot(1.-col3, 1.-col3)/sqrt(3.)),1.)
        ;
    fragColor=vec4(col3.rgb*1.+col5.rgb*0.1,1.);
}
