renderpass.code:
float levels = 2.0;
float gamma = 2.2;

float quantize (float shade) {
    float fac = levels - 1.0;
	return round(shade * fac) / fac;
}

float noise(vec2 coord) {
    vec2 size = vec2(textureSize(iChannel0, 0));
	return texture(iChannel0, coord / size).x;
}

float to_srgb (float shade) {
    return pow(shade, 1.0 / gamma);
}

float to_linear(float shade) {
    return pow(shade, gamma);
}

float dither(vec2 coord, float shade) {
  return floor(noise(coord) + to_linear(shade));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float bands = 5.0;
    float shade = uv.x;
	float result;
    if (uv.y < 1.0 / bands) {
    	result = quantize(shade);
    } else if (uv.y < 2.0 / bands) {
        result = shade;
    } else if (uv.y < 3.0 / bands) {
        result = dither(fragCoord, shade);
    } else if (uv.y < 4.0 / bands) {
        result = shade;
    } else if (uv.y < 5.0 / bands) {
        result = floor(noise(fragCoord) + shade);
    }

    fragColor.rgb = vec3(result) * 1.0;
}
