float Circle( vec2 uv , vec2 p ,float radius , float blur)
{
    float d = length(uv - p)  ;

    float c = smoothstep(radius,radius-blur,d);
    return c ;
}


void mainImage( out vec4 fr, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv -=.5;
    uv.x *= ( iResolution.x  /  iResolution.y) ;
    vec3 clr ;

    float h = sin(iTime );
    float c = Circle(uv ,vec2(.2 , .1) , .4 , .4)  ;
    c -= Circle(uv ,vec2(.9 , .1) , .3 , .4) ;

    clr = vec3(.9, .1 , .6)*c;

    fr = vec4(clr,1.);
}
