#define PI 3.14159265359

vec2 tile(vec2 st, float n){
    st = st*n;
    return fract(st);
}


float box2(in vec2 st, in vec2 size){
    size = vec2(0.5) - size*0.5;
    vec2 uv = smoothstep(size,
                        size+vec2(0.001),
                        st);
    uv *= smoothstep(size,
                    size+vec2(0.001),
                    vec2(1.0)-st);
    return uv.x*uv.y;
}
float cross2(in vec2 st, float size){
    return  box2(st, vec2(size,size/4.)) +
            box2(st, vec2(size/4.,size));
}

mat2 rotate2d(float angle){
    return mat2(cos(angle),-sin(angle),
                sin(angle),cos(angle));
}

bool keyToggle(int ascii) 
{
	return (texture(iChannel0,vec2((.5+float(ascii))/256.,0.75)).x > 0.);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float tiles = 8.;
    vec2 titled = tile(uv,tiles);
    titled -= 0.5;
    titled *= rotate2d(PI * 0.25); 
    titled += 0.5;

    float cr = cross2(titled,2.6 + (sin(iTime) * 1.4));

    vec3 color = vec3( keyToggle(49)
                      ? step(0.,cr) * step(cr,1.)
                      : step(0.5,cr) * step(cr,1.));

    // color += cr;
    fragColor = vec4(color,1.0);
}
