void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    vec2 r = vec2( fragCoord.xy - 0.5*iResolution.xy );

	r = 3.0 * r.xy / iResolution.xy;

    vec3 col1 = vec3 (0.2,0.2,.2);
    vec3 col2 = vec3 (0.0,0.8,.9);
     vec3 col3 = vec3 (.7,.7,.7);
    vec3 pixi;

    float width = (sin(.2 * iTime)*50.);
    float width2 = sin(.003 * iTime)*3.;
    float mody = mod(width/width2,floor(cos(r.x+iTime)*10.));
    if(r.y > mody-width*(sin(.3*iTime)*.3)){

        pixi = col1;

    	}
    else {

        pixi = col2;

        }
      if(r.y < mody*(cos(.03*iTime)/10.)){

        pixi = col3;

    	}

    fragColor = vec4((pixi-sin(r.x)/-mody),1.0);
}
