void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord.xy - iResolution.xy *0.5)/iResolution.y;

    float SIZE = iResolution.x * 0.1;

    vec2 ouv = fragCoord.xy / iResolution.xy;

    uv = fract(uv*SIZE);

    ouv.x = floor(ouv.x*SIZE)/SIZE;
    vec3 bp = texture(iChannel0, ouv).rgb;
    float bpTint = (bp.r + bp.g + bp.b) * 0.33; 

    float d = 1. - smoothstep(bpTint, bpTint + 0.1, abs(uv.x-0.5));                                                                                            
    vec3 col = vec3(d);    

    fragColor = vec4(col,1.0);
}
