vec3 black = vec3(0.0,0.0,0.0);
vec3 maroon = vec3(0.7,0.0,0.0);
vec3 blue = vec3(0.5,0.5,.5);
vec3 yellow = vec3(0.9,0.9,0.0);
vec3 white = vec3(1.0,1.0,1.0);

float bird(vec2 uv, vec2 pos, float r)
{   
    float dist = distance(pos, uv) * r;
    float value = step(r, dist);

    uv -= pos;

    float angle = (atan(uv.y, uv.x) - 4.7);

    float radius = abs(cos(angle * 1.0) * sin(angle * 3.0)) * sin(angle * 3.0) * 0.7 + 0.5;

    value = step(radius , dist);

    return value;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float mBird = bird(uv, vec2(tan(0.9 * iTime) / 1.5 + 0.9, 0.5), 3.0);
    vec3 spiCol = mix( tan(col + iTime) + 1.0, black, mBird);

    // Output to screen
    fragColor = vec4(spiCol,1.0);
}
