#define	AXIS_THICKNESS		1.0
#define GRID_THICKNESS		1.0
#define GRAPH_THICKNESS		2.0
#define	X_SPACING			50.0
#define Y_SPACING			50.0

float f(float x){
    // Plotting function
    float y = x;
    return y;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    // Pre-emptive distance calculations
    float xdist = distance(fragCoord.x, iResolution.x * 0.5);
    float ydist = distance(fragCoord.y, iResolution.y * 0.5);

    // Draw main grid
    if ((distance(fragCoord.x, iResolution.x * 0.5) < AXIS_THICKNESS) || 
        (distance(fragCoord.y, iResolution.y * 0.5) < AXIS_THICKNESS)){
    	fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }

    // Draw gridlings
    else if (xdist - (X_SPACING * floor(xdist/X_SPACING)) > X_SPACING - GRID_THICKNESS || 
             ydist - (Y_SPACING * floor(ydist/Y_SPACING)) > Y_SPACING - GRID_THICKNESS){
    	fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }

    // Plotting happens here!
    else if ((fragCoord.y - f(fragCoord.x)) < GRAPH_THICKNESS && 
             (fragCoord.y - f(fragCoord.x)) > -GRAPH_THICKNESS){
    	fragColor = vec4(0.0, 0.0, 1.0, 1.0);
    }

    // White fill the rest of the screen
    else {
    	fragColor = vec4(1.0, 1.0, 1.0, 1.0);
    }
}
