void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //vec4 p=gl_FragCoord/1e3;
    vec4 p = vec4(fragCoord,0,1)/iResolution.y;
    float t = iTime;
    float s = sound(t);
    fragColor.z=2.*s*.9/abs(1.0-length(p+sin(t+p.y*s*1e2)));
}

vec2 mainSound( in int samp, float t )
{
    vec2 b;
    for(int i;i++<3;t*=.99)
        b+=sound(t);
    return b*sin(t*.8)/1e1;
}
