 vec3 v_normal;
 vec2 v_texcoord;


//fonction rotate
mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float croix (vec2 pos) {

float epaisseur = 0.008;
float x= step(abs(pos.x),epaisseur);
float y= step(abs(pos.y),epaisseur);
return x + y;
}



float fleur(vec2 uv,float rayon) {

vec2 pos = vec2(0.5)-uv;
float a = atan(pos.x,pos.y) *4. +7.85;

float distan =  distance(vec2 (0.5), uv);
rayon *= abs(cos(a))*0.15 +0.5;  

float fleur = step(rayon , distan);
fleur += croix(pos* rotate2d(0.785)) +croix(pos); // on soustrait avec les croix

return fleur;
}

vec2 impair (vec2 uv) {  vec2 impair = vec2(1.);
 impair.y = step(1.,mod(uv.y,2.)); 
 impair.x= step(1.,mod(uv.x,2.));


return impair; }




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv = gl_FragCoord.xy /iResolution.xy;



     float a = atan(uv.x,uv.y);
     a +=  cos(iTime+70.) * (1.-a)  ;


    uv.x += 0.5;
    vec2 uvfract = uv* 10.;
    vec2 impair= impair(uvfract);
    uv.x = uv.x+ 0.3*iTime * impair.y;

    uv= fract(uv*10.);




    vec3 color= vec3(fleur(uv, 0.6 * cos(iTime)*a));
    fragColor = vec4(color,1.);
}
