//Color flashes.
//Mouse: X - speed of color changing, Y - freq of flash. Start from left bottom corner.
//CAUTION !!! Apply carefully at own risk !!!. 
//It may cause an epilepsy attack.

// Nice color transitions
// Mouse X - color change velocity
// Mouse Y - flash frequency  

void mainImage(out vec4 zyx, vec2 xy)
{
    vec2 x = iMouse.xy/iResolution.xy + 0.003;

    vec2 y = xy/iResolution.xy - 0.5;

    vec2 z = vec2 (y*(cos(60. * x.y * iTime + vec2 (0,.75))));

    vec3 xyz = vec3 (1.- length(z )) *
               vec3 (.3+cos(3.26 * x.x *  iTime)/2.,  
                     .6+cos(2.13 * x.x *  iTime)/2.,
                     .3+cos(1.09 * x.x *  iTime)/2.);

    zyx = vec4(xyz,1.0);
}
