void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Drawing texture
    vec2 point = fragCoord.xy / iResolution.xy;
    vec4 texAtPoint = texture(iChannel0, point);

    float medianValue = (texAtPoint.x + texAtPoint.y + texAtPoint.z) / 3.0;
    texAtPoint = vec4(medianValue, medianValue, medianValue, 1.0);

    // Rendering
    fragColor = texAtPoint;
}
