void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 lightVector_UVTangentSpace = normalize(iMouse.xy / iResolution.xy - 0.5);
	vec2 lightVectorRotated90deg = vec2(-lightVector_UVTangentSpace.y, lightVector_UVTangentSpace.x);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    vec2 centeredUV = uv - 0.5f;

    vec2 rotatedUV = vec2(dot(centeredUV, lightVector_UVTangentSpace), dot(centeredUV, lightVectorRotated90deg));

    vec2 finalUV = rotatedUV + 0.5f;


    fragColor = texture(iChannel0, finalUV);
}
