vec3 paintCircleIn(vec2 center, float step,  vec2 uv, float timer, float width){
	float len = length(uv - center)+0.0001;
    //float ang = asin(uv.y / len);
    float sum = 0.0;
    for (float i = 0.0;i<1.0;i+=0.2){
    	float circle = smoothstep(i-width, i, len)-smoothstep(i, i+width, len);
        sum = circle;
        if(circle != 0.0){
        break;
        }
    }
    return vec3(sum);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = paintCircleIn(vec2(0.5,0.5), 0.1, uv, iTime, 0.025);
        //0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}
