void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel1, uv).g>0.?
        float4(.1,.3,.6,0):float4(.5,0.6,0.7,1);
	fragColor = texture(iChannel0, uv).b>0.?float4(0.9):fragColor;
}

float dist(float2 cell, float2 xy)
{
    float r=hash(cell.x+cell.y*1027.0);

    if(r>0.5)
	    xy=float2(xy.x,-xy.y);
    float d=abs(fract(xy.x+xy.y)-0.5)*20.-7.75;
	return clamp(d,0.,1.);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv=fragCoord.xy/10.;
    uv=float2(uv.x+uv.y,uv.x-uv.y);
    float2 ixy=floor(uv);
    float2 xy=(fract(uv)-0.5);

	float d=dist(ixy,xy);


	fragColor = vec4(0,0,d,1.0);
}

#define float3 vec3
#define float2 vec2
#define float4 vec4


float hash(float p)
{
	float3 p3  = fract(vec3(p) * .157);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

float4 T(float2 uv)
{
    return texture(iChannel1, uv);
}

float4 M(float2 uv)
{
    return texture(iChannel0, uv);
}

void mainImage(out float4 C, in float2 XY)
{
	if(iFrame==0)
    {
    	if(length(XY-float2(140,99))<1.)
        	C=float4(1);
        else
        	C=float4(0);
        return;
    }

    float2 uv=XY/iResolution.xy;
    float2 dx=float2(1.0/iResolution.x,0);
    float2 dy=float2(0,1.0/iResolution.y);

    float4 m=M(uv);
	float4 t=T(uv);
    C=t;

    if(iMouse.z>0.)
    {
        if(length(XY-iMouse.zw)<1.)
        	C=float4(1);
        else
        	C=float4(0);
        return;
    }

    if(m.b==0.)
    {
        if(t.g==0.)
        {
			float4 t0;
            if(
                ((t0=T(uv+dx)).g>0.)||
                ((t0=T(uv-dx)).g>0.)||
                ((t0=T(uv-dy)).g>0.)||
                ((t0=T(uv+dy)).g>0.)
              )
            {

                C=float4(1);
                return;
            }
        }
    }

}
