renderpass.code:
float dots( vec2 uv )
{
 	return step(1., mod(uv.x, 2.0)) * step(1., mod(uv.y, 2.0));
}

float dashes( vec2 uv )
{
 	return step(3., mod(uv.x, 8.0)) * step(7., mod(uv.y, 8.0));   
}

vec2 rotate ( vec2 uv )
{
    return vec2( uv.y, uv.x );
}

float dashes2 ( vec2 uv )
{
 	return dashes(uv) + dashes(uv + vec2(4));   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //vec2 uv = fragCoord/iResolution.xy;

    vec2 uv = fragCoord / 8.0;


	float col = 0.;

    col = dashes2(uv) + dashes2(rotate(uv) + vec2(2, 2)) + dots(uv);

    vec3 c = mix(vec3(1.), vec3(.7, .4, .3), step(0.5, col));

    fragColor = vec4(c, 1.0);
}
