void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    float radii=.25+log(iTime*5.)*.05;


    float current_distance = length(uv-vec2((iResolution.x/iResolution.y)/2., 0.5));
    vec3 col = vec3((radii-current_distance)*sin(iTime*2.)*10.+0.5, 0, (radii-current_distance)*sin(iTime*8.)+0.5);
    if(current_distance > radii)
        col=col*vec3(current_distance/radii, current_distance/radii, current_distance/radii);

    // Output to screen
    fragColor = vec4(col,0.0);
}
