float hash11(float p)
{
	vec3 p3  = fract(vec3(p) * 443.8975);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

vec3 hash31(float p)
{
   vec3 p3 = fract(vec3(p) * vec3(443.897, 441.423, 437.195));
   p3 += dot(p3, p3.yzx+19.19);
   return fract((p3.xxy+p3.yzz)*p3.zyx); 
}

float D(vec2 p, float t) {
	float d = 9.;
    for(float i = 0.; i < 100.; i++) {
        vec3 c = fract(hash31(i) - vec3(.65,.65,1)*t);
    	d = min(d, length(p - (c.xy*2.-1.) / c.z)-(.003 + hash11(i)*.005)/c.z);
    }
    return d;
}

#define B 8.

#define mainImage(O,u)\
	vec2 R = iResolution.xy, p = (u+u - R)/R.y;						\
    float a = 0.;													\
    for(float i = 0.; i < B; i++)									\
        a += smoothstep(3./R.y, 0., D(p, iTime - i / (30. * B)));	\
																	\
    O.rgb = mix(mix(vec3(.95,.95,.98), vec3(.6,.6,.99),				\
            pow(u.y/R.y, .3)), vec3(1), a/B);
