float getVal(vec2 uv) {
    // return length(texture(iChannel0,uv).xyz);
    return dot(texture(iChannel0,uv).xyz, vec3(0.299, 0.587, 0.114));
}

vec2 getGrad(vec2 uv,float delta) {
    vec2 d=vec2(delta,0);
    return vec2(
        getVal(uv+d.xy)-getVal(uv-d.xy),
        getVal(uv+d.yx)-getVal(uv-d.yx)
    )/delta;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // get original color
    vec4 texColor = texture(iChannel0, uv);
    vec3 n = vec3(getGrad(uv,1.0/iResolution.y), iResolution.y*0.95);
    // n *= n;
    n=normalize(n);
    fragColor = vec4(n,1);	// show debug
    vec3 light = normalize(vec3(1,1,2));
    float diff=clamp(dot(n,light),0.5,1.0);
    float spec=clamp(dot(reflect(light,n),vec3(0,0,-1)),0.0,1.0);
    spec=pow(spec,36.0)*1.5;
	fragColor = texColor * vec4(diff) + vec4(spec);

    // Compare with original
    if(uv.x > 0.5 + 0.0002 * sin(iTime)) fragColor = texColor;
}
