renderpass.code:
vec3 brown = vec3(0.3,0.2,0.2);
vec3 black = vec3(0.0,0.0,0.0);
vec3 red = vec3(0.7,0.0,0.0);
vec3 blue = vec3(0.,1.,1.);
vec3 yellow = vec3(1.0,1.0,0.0);
vec3 green = vec3(0.1,0.9,0.0);
vec3 dirty = vec3(.4,0.6,.4);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float offset = fract(iTime * 1.0) * 2.0;

    float height = step(0.25,uv.y) - step(0.45,uv.y);
    float width = step(0.,uv.x) -step(0.05,uv.x);

    vec3 rect = mix(black, dirty, height * width);

    //Bullet
    height = step(0.44,uv.y) - step(0.47,uv.y);
    width = step(offset + .0,uv.x) - step(offset + 0.05,uv.x);

    rect = mix(rect, red, height * width);

    height = step(0.4,uv.y) - step(0.49,uv.y);
    width = step(0.,uv.x) -step(0.16,uv.x);

    rect = mix(rect, dirty, height * width);

    height = step(0.33,uv.y) - step(0.55,uv.y);
    width = step(0.,uv.x) -step(0.15,uv.x);

    rect = mix(rect, brown, height * width);



    // Output to screen
    fragColor = vec4(rect,1.0);
}
