void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pixelSize = 1.1 / iResolution.xy;
    vec2 uv = fragCoord.xy * pixelSize;

    if(uv.x > 1. - pixelSize.x){
    	fragColor = texture(iChannel0, uv.yx * vec2(0.8+sin(iTime*1.4),-0.8-cos(iTime*1.4)) + vec2(0.,1.));
    }else{
        fragColor = texture(iChannel1, uv + vec2(1.0,0) * pixelSize);
    }
}
