//==============================================================
// Functions for 1,2,3 Noise by Patricio Gonzalez Vivo
// Ref: https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83
float rand(vec2 n) {
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}
float noise(vec2 n) {
	const vec2 d = vec2(0.0, 1.0);
	vec2 b = floor(n), f = smoothstep(vec2(0.0), vec2(1.0), fract(n));
	return mix(mix(rand(b), rand(b + d.yx), f.x), mix(rand(b + d.xy), rand(b + d.yy), f.x), f.y);
}


//=============================================================
// R2 low-discrepancy sequence as described by Martin Roberts
// Ref: http://extremelearning.com.au/unreasonable-effectiveness-of-quasirandom-sequences
const float a1 = 0.61803398874989484820633884919255;
const float a2 = 0.56984029099805326591218186327522;
const float a3 = 0.54970047790197026694714643076746;
float R_2_1D(float n) {
	return (0.5 + a1 * n);
}
vec2 R_2_2D(float n) {
	return vec2(0.5 + a1 * n, 0.5 + a2 * n);
}
vec3 R_2_3D(float n) {
	return vec3(0.5 + a1 * n, 0.5 + a2 * n, 0.5 + a3 * n);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) { 
    float uv_scale = 1.0;
    vec2 uv_displacement = vec2(10000,10000) + vec2(float(iFrame) * 1.75);
    float coord = noise(fragCoord * uv_scale + uv_displacement);
    vec3 pattern = R_2_3D(coord);
    vec3 fabricColor = vec3(0.7, 0.5, 0.41);
    fragColor = vec4(pattern * fabricColor, 1);
}
