#define RES 100.0
#define SPEED 1.0

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 noiseInput = floor(uv * RES);

	vec3 col = vec3(sin(rand(noiseInput) * iTime * 3.14159 * 2.0 * SPEED));

	vec4 color = vec4(vec3(col), 1.0);
    color.r *= (uv.y + cos(iTime) * 1.0 + 1.0);
    //color.b *= ((uv.x + uv.y) / 2.0 * cos(iTime * 1.5) + 1.0);

    // Output to screen
    fragColor = color;
}
