void mainImage( out vec4 c, in vec2 f )
{ 
    c.rgb = texture(iChannel0,f/iResolution.xy).rgb;
}

#define px 3./iResolution.y
#define PI 3.1415927
#define PHI 1.61803398875
#define SS(A,B,C) smoothstep(A,B,C)
#define circle(A,B) SS(px,0.,length(A)-B)*SS(0.,px,length(A)-B/1.5)
#define rot(A) mat2(cos(A),-sin(A),sin(A),cos(A))

void mainImage(out vec4 c, in vec2 f)
{
    vec2 uv = (2.0*f.xy-iResolution.xy) / iResolution.y;
    float n = 1500.,size = .01, o = 0.;
    for(float i=0.;i<n;i++)
    {        
        size*=.9985;
        uv*=rot(PHI*sin(iTime/400.0));
        o+=circle(uv+vec2(0,1.5*i/n),size);
    }
    c.rgb=vec3(o)+.65*texture(iChannel0,f/iResolution.xy).rgb*vec3(cos(uv.x-iTime),sin(uv.y+iTime),sin(iTime-uv.y/10.0));
}
