void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 moose = iMouse.xy/iResolution.xy; 
	vec2 p1 = vec2(.5, .5);
    vec2 p2 = vec2(moose.x + .45, moose.y);
    vec2 p3 = vec2(moose.x + .05, moose.y);
    vec2 p4 = vec2(moose.x + .25, moose.y + .25);
    vec3 col = vec3(0.0, 1.0, 0.0) * cos(min(min(length(uv-p1), length(uv-p2)), min(length(uv-p3), length(uv-p4))) * 1000.0 + 5.0 * iTime);

    // Output to screen
    fragColor = vec4(col,1.0);
}
