void mainImage( out vec4 f, vec2 w ) {

    vec4 p = vec4(w,0,1)/iResolution.xyxy-.5, d=p,
    m = iMouse/6e1, t;
    for (p.z = iTime;;){
          t = abs((mod(p, 8.)-4.)*(mod(p, 3.)-1.5));
          f = texture(iChannel0, t.zyx-m.x) 
              +.1* vec4( sin(.3*iTime*p.z),sin(.2*p.z),sin(.5*p.z),1.);
          w.x = min(t.y, length(t.xyz)-m.y);
          if(w.x<.01) break;
          p -= d*w.x; 
    }
}
