#define SCALE 64.0

float circle(in vec2 _st, in float _radius){
    vec2 l = _st-vec2(0.5);
    return 1.-smoothstep(_radius-(_radius*0.01),
                         _radius+(_radius*0.01),
                         dot(l,l)*4.0);
}

float rand1(vec2 co){
    return fract(sin(dot(co.xy ,vec2(4.2574,72.233))) * 43758.5453);
}

float rand2(vec2 x) {
    int n = int(x.x * 40.0 + x.y * 6400.0);
    n = (n << 13) ^ n;
    return 1.0 - float( (n * (n * n * 15731 + 789221) + \
             1376312589) & 0x7fffffff) / 1073741824.0;
}

vec2 posFragInsideGrid(vec2 stScaled) {
    return vec2(floor(stScaled)+1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord) {
	vec2 st = gl_FragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;
    vec3 color = vec3(0.3);

    vec2 stScaled = st*SCALE; // Scale up the space by SCALE value
    vec2 stTiled = fract(stScaled); // Wrap arround 1.0

    vec2 posFrag = posFragInsideGrid(stScaled);
    float randomRadius = rand2(posFrag)*rand2(posFrag); // random according to the position in grid.

   	float variationRadius = 4.0*(cos(iTime)+1.0)/2.0;

    color += vec3(circle(stTiled,randomRadius*variationRadius))*vec3(0.5,0.5,0.5);
	fragColor = vec4(color,1.0);
}
