// Lines 12-15 from lines 301-304 of https://www.shadertoy.com/view/Msl3Rr
// Lines 30-end from https://github.com/ashima/webgl-noise/blob/master/src/noise3D.glsl

float f[4];
float snoise(vec3 v);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    f[0] = texture( iChannel0, vec2( 0.01, 0.25 ) ).x;
	f[1] = texture( iChannel0, vec2( 0.07, 0.25 ) ).x;
	f[2] = texture( iChannel0, vec2( 0.15, 0.25 ) ).x;
	f[3] = texture( iChannel0, vec2( 0.30, 0.25 ) ).x;

    vec3 d = vec3(2.68, 2.24, 3.29);
    float t_div = 2.0;

    float fr = iTime / t_div;

    vec3 col = vec3(
      snoise(vec3(uv.x * d.r + f[0], uv.y * d.r + f[3], fr       )) + f[0],
      snoise(vec3(uv.x * d.g + f[1], uv.y * d.g       , fr + f[0])) + f[1],
      snoise(vec3(uv.x * d.b + f[2], uv.y * d.b       , fr       )) + f[2]
    );
    fragColor = vec4(col,0); 
}

