#define t iTime

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy - 0.5)/vec2(iResolution.y/iResolution.x, 1);

    vec2 pa = uv + .1*vec2(sin(sin(t)*2.),cos(sin(t*.8)+t));
  	vec2 pb = uv + .6*vec2(sin(t*.9),cos(sin(t*.2)*2.9));
  	float d = fract(length(pa)*10.) * 5.*fract(length(pb)*2.);

    d = fract(d);
  	float w = .7+.2*sin(t*2.);
    float a = 0.1;
    float c = (smoothstep(1.-a-w,1.-w,d) - smoothstep(w,w+a,d));

    fragColor = vec4(vec3(c),1.0);
}
