vec4 circle(vec2 uv, vec2 pos, float rad, vec3 color) 
{
	float d = length((pos) - uv) - rad;
	float t = clamp(d, 0.0, 2.0);

	return vec4(color, 1.0 - t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = fragCoord.xy;

	vec2 center = iResolution.xy * 0.5;
	float radius = 0.25 * iResolution.y;


    // Background layer

	// Circle
	vec3 orange = vec3(1.0, 0.5, 0.3);
	vec4 circle = circle(uv, center , tan(iTime)*radius, orange);

     uv = fragCoord.xy / iResolution.xy;

    vec4 col = 0.5 + 0.5*sin(iTime+uv.xyxx+vec4(0,2,4,0));

	// Blend the two
	fragColor = mix(col, circle, circle.a);

}

